import { useContext } from 'react'
import { useQuery } from 'react-query'

import {
  CONTRACT_ADDRESSES,
  MAINNET_POLLING_INTERVAL,
  QUERY_KEYS,
} from 'lib/constants'
import { AuthControllerContext } from 'lib/components/contextProviders/AuthControllerContextProvider'
import { fetchExternalLootBoxData } from 'lib/utils/fetchExternalLootBoxData'

export function useEthereumLootBoxQuery(pool) {
  const { chainId, pauseQueries, provider } = useContext(AuthControllerContext)

  const lootBoxControllerAddress = CONTRACT_ADDRESSES[chainId]?.LootBoxController
  const filterAddress = CONTRACT_ADDRESSES[chainId].LootBox

  const erc721 = pool?.prizeStrategy?.externalErc721Awards?.find(erc721 => erc721.address === filterAddress)
  // const { lootBoxAddress, tokenId } = useGraphLootBoxQuery(lootBoxAddress, tokenId)
  const tokenId = erc721?.tokenIds?.[0]

  const enabled = !pauseQueries &&
    chainId &&
    Boolean(lootBoxControllerAddress) &&
    Boolean(tokenId)

  return useQuery(
    [QUERY_KEYS.ethereumLootBoxQuery, chainId, lootBoxControllerAddress, tokenId, -1],
    async () => await fetchExternalLootBoxData(provider, lootBoxControllerAddress, tokenId),
    {
      enabled,
      refetchInterval: MAINNET_POLLING_INTERVAL
    }
  )
}
