    chainId &&
    Boolean(lootBoxAddress) &&
    Boolean(tokenId)

  return useQuery(
    [QUERY_KEYS.ethereumLootBoxQuery, chainId, poolAddress, -1],
    async () => await fetchExternalLootBoxData(params),
