import { useQuery } from 'react-query'

import {
  MAINNET_POLLING_INTERVAL,
  QUERY_KEYS,
} from 'lib/constants'
import { fetchExternalLootBoxData } from 'lib/utils/fetchExternalLootBoxData'

export function useEthereumLootBoxQuery(pauseQueries, chainId, lootBoxAddress, tokenId) {
  const enabled = !pauseQueries &&
    chainId &&
    Boolean(lootBoxAddress) &&
    Boolean(tokenId)

  return useQuery(
    [QUERY_KEYS.ethereumLootBoxQuery, chainId, poolAddress, -1],
    async () => await fetchExternalLootBoxData(params),
    {
      enabled,
      refetchInterval: MAINNET_POLLING_INTERVAL
    }
  )
}
