  CONTRACT_ADDRESSES,
  MAINNET_POLLING_INTERVAL,
  QUERY_KEYS,
} from 'lib/constants'
import { AuthControllerContext } from 'lib/components/contextProviders/AuthControllerContextProvider'
import { fetchExternalLootBoxData } from 'lib/utils/fetchExternalLootBoxData'

export function useEthereumLootBoxQuery(lootBoxAddress, tokenId) {
  const { chainId, pauseQueries } = useContext(AuthControllerContext)

  console.log(pool)
  const filterAddress = CONTRACT_ADDRESSES[chainId].LootBox
  const erc721s = pool?.prizeStrategy?.externalErc721Awards?.find(erc721 => erc721.address === filterAddress)
  console.log(filterAddress)
  console.log(erc721s)

  // const { lootBoxAddress, tokenId } = useGraphLootBoxQuery(lootBoxAddress, tokenId)

  const enabled = !pauseQueries &&
    chainId &&
    Boolean(filterAddress) &&
    Boolean(tokenId)

  return useQuery(
    [QUERY_KEYS.ethereumLootBoxQuery, chainId, filterAddress, tokenId, -1],
    async () => await fetchExternalLootBoxData(provider, filterAddress, tokenId),
    {
      enabled,
      refetchInterval: MAINNET_POLLING_INTERVAL
    }
  )
}
